	private final Consumer<RecipeExporter> all(ItemConvertible base, ItemConvertible slab, ItemConvertible stairs, ItemConvertible wall, Object2IntMap<ItemConvertible> stonecutting) {
		return exp -> {
			offerSlabRecipe(exp, RecipeCategory.BUILDING_BLOCKS, slab, base);
			StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(base), RecipeCategory.BUILDING_BLOCKS, slab, 2).criterion(hasItem(base), conditionsFromItem(base)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(slab, base) + "_stonecutting"));
			offerStairsRecipe(exp, stairs, base);
			StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(base), RecipeCategory.BUILDING_BLOCKS, stairs).criterion(hasItem(base), conditionsFromItem(base)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(stairs, base) + "_stonecutting"));
			offerWallRecipe(exp, RecipeCategory.DECORATIONS, wall, base);
			StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(base), RecipeCategory.DECORATIONS, wall).criterion(hasItem(base), conditionsFromItem(base)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(wall, base) + "_stonecutting"));
			
			for (ItemConvertible item : stonecutting.keySet()) {
				StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(item), RecipeCategory.BUILDING_BLOCKS, slab, 2 * stonecutting.applyAsInt(item)).criterion(hasItem(item), conditionsFromItem(item)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(slab, item) + "_stonecutting"));
				StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(item), RecipeCategory.BUILDING_BLOCKS, stairs, stonecutting.applyAsInt(item)).criterion(hasItem(item), conditionsFromItem(item)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(stairs, item) + "_stonecutting"));
				StonecuttingRecipeJsonBuilder.createStonecutting(Ingredient.ofItems(item), RecipeCategory.DECORATIONS, wall, stonecutting.applyAsInt(item)).criterion(hasItem(item), conditionsFromItem(item)).offerTo(exp, Identifier.of(SSWPlus.MOD_ID, convertBetween(wall, item) + "_stonecutting"));
			}
		};
	}
	
